// Type definitions for path-starts-with 2.0.0
// Project: https://github.com/jonschlinkert/path-starts-with
// Definitions by: sounisi5011 <https://github.com/sounisi5011>

interface Options {
    /**
     * Disable case sensitivity.
     * @default false
     * @example
     * startsWith('foo/bar', 'FOO');
     * //=> false
     * startsWith('foo/bar', 'FOO', {nocase: true});
     * //=> true
     */
    readonly nocase?: boolean;
    /**
     * Allow partial matches.
     * @default false
     * @example
     * startsWith('foobar', 'foo');  //=> false
     * startsWith('foo.bar', 'foo'); //=> false
     *
     * startsWith('foobar', 'foo', {partialMatch: true});  //=> true
     * startsWith('foo.bar', 'foo', {partialMatch: true}); //=> true
     */
    readonly partialMatch?: boolean;
    readonly nonegate?: boolean;
}

declare function startsWith(
    filepath: string,
    substr: string,
    options?: Options,
): boolean;

export = startsWith;
