// Type definitions for npm-install-checks 4.0.0
// Project: https://github.com/npm/npm-install-checks
// Definitions by: sounisi5011 <https://github.com/sounisi5011>

/**
 * @throws {Error} Will throw an error if the unsupported engine.
 *     The error message is `"Unsupported engine"` and the `code` property value is `"EBADENGINE"`.
 */
export function checkEngine(
    target: { readonly engines?: { readonly node?: string; readonly npm?: string } },
    npmVer: string | null | undefined,
    nodeVer?: string | null,
    force?: boolean,
): void;

/**
 * @throws {Error} Will throw an error if the unsupported platform.
 *     The error message is `"Unsupported platform"` and the `code` property value is `"EBADPLATFORM"`.
 */
export function checkPlatform(
    target: { readonly os?: string | readonly string[]; readonly cpu?: string | readonly string[] },
    force?: boolean,
): void;
